package augustopadilha.clientdistributedsystems.system.utilities;

import io.jsonwebtoken.Claims;
import io.jsonwebtoken.Jwts;

import java.io.File;
import java.io.FileWriter;
import java.io.IOException;
import java.util.Scanner;

public class Token {
    private static final String FILE_PATH = "jwt_token.txt";
    private static final String SECRET_KEY = "";

    public static void saveJwtToken(String token) {
        try (FileWriter writer = new FileWriter(FILE_PATH)) {
            writer.write(token);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public static String getJwtToken() {
        try (Scanner scanner = new Scanner(new File(FILE_PATH))) {
            if (scanner.hasNext()) {
                return scanner.nextLine();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
        return null;
    }

    public static boolean eraseJwtToken() {
        File jwt = new File(FILE_PATH);

        if (jwt.exists()) {
            return jwt.delete();
        }

        return false;
    }

    public static boolean isTokenAdmin(String token) {
        Claims claims = Jwts.parser()
                .setSigningKey(SECRET_KEY)
                .parseClaimsJws(token)
                .getBody();

        return (boolean) claims.get("admin", Boolean.class);
    }

}
